const affichageTeddies=document.getElementById("section-produits"),affichageListeTeddies=teddy=>{affichageTeddies.innerHTML+=`<div class= "card">\n        <img src=${teddy.imageUrl} alt="ours en peluche" class="card-img-top"/>\n       <div class="card-body">\n         <h3 class='card-title'>${teddy.name}</h3>\n         <p class='card-text'>${teddy.description}</p>\n         <div class="card-price-star">\n            <span class='card-price'>prix: ${teddy.price/100}.00€</span>\n            <span class="star-blue"><i class="fas fa-star"></i></span>\n            <span class="star-blue"><i class="fas fa-star"></i></span>\n            <span class="star-blue"><i class="fas fa-star"></i></span>\n            <span class="star-blue"><i class="fas fa-star"></i></span>\n            <span class="star-blue"><i class="fas fa-star"></i></span>\n         </div>\n         <a href="./product.html?id=${teddy._id}" class="btn btn-primary" aria-label="Bouton Accès page produits"> En savoir plus</a>\n        </div> \n       </div>`};function getTeddiesProducts(){fetch("http://localhost:3000/api/teddies").then(response=>response.json()).then((function(productslist){for(let teddy of productslist)affichageListeTeddies(teddy)})).catch((function(error){console.log("la liste des produits en vente est temporairement indisponible")}))}getTeddiesProducts();